import React, { useState, useEffect } from "react";
import { Box, Typography, Button, TextField } from "@mui/material";
import heroBg from "../images/hero-bg.jpg"; // Import your hero background image
import ".styles/HeroSection.css";

const SurveyForm = () => (
  <form>
    <TextField
      label="What's your biggest challenge?"
      variant="outlined"
      fullWidth
      margin="normal"
    />
    <TextField
      label="Enter your email"
      variant="outlined"
      fullWidth
      margin="normal"
    />
    <Button variant="contained" color="primary" fullWidth>
      Submit
    </Button>
  </form>
);

const HeroSection = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > window.innerHeight - 80); // Trigger when scrolling past HeroSection
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <Box
      sx={{
        height: "100vh", // Cover the full viewport height
        backgroundImage: `url(${heroBg})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        position: "relative", // Needed for overlaying MenuBar
      }}
    >
      <Box
        className={`menu-bar ${isScrolled ? "scrolled" : ""}`}
        sx={{
          position: "absolute",
          top: 0,
          width: "100%",
          zIndex: 10,
          transition: "background-color 0.3s ease",
          backgroundColor: isScrolled ? "#ffffff" : "transparent",
          boxShadow: isScrolled ? "0 4px 6px rgba(0, 0, 0, 0.1)" : "none",
        }}
      >
        <Box
          sx={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            padding: "10px 20px",
          }}
        >
          <Typography variant="h6" color={isScrolled ? "black" : "white"}>
            CPA Landing
          </Typography>
          <Box sx={{ display: "flex", gap: "15px" }}>
            <Button
              color="inherit"
              sx={{ color: isScrolled ? "black" : "white" }}
            >
              Home
            </Button>
            <Button
              color="inherit"
              sx={{ color: isScrolled ? "black" : "white" }}
            >
              About
            </Button>
            <Button
              color="inherit"
              sx={{ color: isScrolled ? "black" : "white" }}
            >
              Contact
            </Button>
            <Button
              color="inherit"
              sx={{ color: isScrolled ? "black" : "white" }}
            >
              Offers
            </Button>
          </Box>
        </Box>
      </Box>
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
          textAlign: "center",
          color: "white",
        }}
      >
        <Typography variant="h3" gutterBottom>
          Find Your Perfect Solution!
        </Typography>
        <Typography variant="h6" gutterBottom>
          Answer a few quick questions and discover our exclusive offers.
        </Typography>
        <Box
          sx={{
            backgroundColor: "rgba(255, 255, 255, 0.9)",
            borderRadius: 2,
            p: 3,
            mt: 3,
            width: "100%",
            maxWidth: 400,
          }}
        >
          <SurveyForm />
        </Box>
      </Box>
    </Box>
  );
};

export default HeroSection;
